import React, { useEffect, useRef, useState } from "react";
import shaka from "shaka-player";
import "shaka-player/dist/controls.css";
import "./VideoPlayer.css";

const VideoPlayer = ({ titre = "Mont-Rouge", sousTitre = "Bande-annonce" }) => {
  const videoRef = useRef(null);
  const playerRef = useRef(null);
  const [isLive, setIsLive] = useState(false);
  const [qualities, setQualities] = useState([]);
  const [selectedQuality, setSelectedQuality] = useState("auto");

  const LIVE_URL = "https://livesim.dashif.org/livesim/testpic_2s/Manifest.mpd";
  const VOD_URL =
    "https://storage.googleapis.com/shaka-demo-assets/angel-one/dash.mpd";

  useEffect(() => {
    // Initialiser Shaka Player
    const initPlayer = async (url = VOD_URL, live = false) => {
      if (!videoRef.current) return;

      // Créer une instance de Shaka Player
      const player = new shaka.Player(videoRef.current);
      playerRef.current = player;

      // Configurer le DRM (exemple avec Widevine)
      const drmConfig = {
        servers: {
          "com.widevine.alpha": "https://license.widevine.com/getlicense",
        },
      };
      player.configure("drm", drmConfig);

      // Configurer la publicité (exemple avec IMA)
      const adConfig = {
        adTagUrl:
          "https://pubads.g.doubleclick.net/gampad/ads?iu=/21775744923/external/single_preroll_skippable&sz=640x480&ciu_szs=300x250%2C728x90&gdfp_req=1&output=vast&unviewed_position_start=1&env=vp&impl=s&correlator=",
      };
      player.configure("ad", adConfig);

      player.addEventListener("adaptation", () => {
        const tracks = player.getVariantTracks();
        setQualities(tracks);
      });

      try {
        // Charger la vidéo (remplacer par ton URL)
        await player.load(url);
        setIsLive(live);
        // Récupérer les qualités après le chargement
        const tracks = player.getVariantTracks();
        setQualities(tracks);
      } catch (error) {
        console.error("Erreur lors du chargement de la vidéo:", error);
      }
    };

    initPlayer();

    // Nettoyer à la destruction du composant
    return () => {
      if (playerRef.current) {
        playerRef.current.destroy();
      }
    };
  }, []);

  const avancer = () => {
    if (videoRef.current) {
      videoRef.current.currentTime += 10;
    }
  };

  const reculer = () => {
    if (videoRef.current) {
      videoRef.current.currentTime -= 10;
    }
  };

  const chargerLive = () => {
    if (playerRef.current) {
      playerRef.current.load(LIVE_URL);
      setIsLive(true);
    }
  };

  const handleQualityChange = (e) => {
    const value = e.target.value;
    setSelectedQuality(value);
    if (playerRef.current) {
      if (value === "auto") {
        playerRef.current.configure({ abr: { enabled: true } });
      } else {
        playerRef.current.configure({ abr: { enabled: false } });
        const track = qualities.find((q) => q.id.toString() === value);
        if (track) {
          playerRef.current.selectVariantTrack(track, /* clearBuffer= */ true);
        }
      }
    }
  };

  return (
    <div className="lecteur-container">
      <div className="lecteur-header">
        <span className="lecteur-retour">&#8592;</span>
        <div>
          <div className="lecteur-titre">{titre}</div>
          <div className="lecteur-sous-titre">{sousTitre}</div>
        </div>
      </div>
      <div className="lecteur-video-wrapper">
        <video ref={videoRef} controls className="lecteur-video" />
        {isLive && <span className="lecteur-live">EN DIRECT</span>}
      </div>
      <div className="lecteur-boutons">
        <button onClick={reculer} className="lecteur-bouton">
          ⏪ 10s
        </button>
        <button onClick={avancer} className="lecteur-bouton">
          10s ⏩
        </button>
        <button onClick={chargerLive} className="lecteur-bouton-live">
          Passer en direct
        </button>
        <select
          value={selectedQuality}
          onChange={handleQualityChange}
          className="lecteur-select-qualite"
        >
          <option value="auto">Qualité : Auto</option>
          {qualities
            .filter(
              (q, idx, arr) =>
                arr.findIndex((t) => t.height === q.height) === idx
            )
            .sort((a, b) => b.height - a.height)
            .map((q) => (
              <option key={q.id} value={q.id}>
                {q.height ? q.height + "p" : q.bandwidth + "bps"}
              </option>
            ))}
        </select>
      </div>
    </div>
  );
};

export default VideoPlayer;
