import React, { useEffect, useRef, useState } from "react";
import shaka from "shaka-player";
import "shaka-player/dist/controls.css";
import "./VideoPlayer.css";

const PLAYLIST = [
  {
    titre: "Live Test (DASH-IF)",
    sousTitre: "Flux en direct de test DASH-IF",
    url: "https://livesim.dashif.org/livesim/testpic_2s/Manifest.mpd",
    live: true,
  },
  {
    titre: "Captain Luck (Angel One)",
    sousTitre: "Bande-annonce",
    // url: "https://storage.googleapis.com/shaka-demo-assets/angel-one/dash.mpd",
    
    live: false,
  },
  {
    titre: "Big Buck Bunny",
    sousTitre: "Démonstration DASH",
    url: "https://dash.akamaized.net/akamai/bbb_30fps/bbb_30fps.mpd",
    live: false,
  },
  {
    titre: "Tears of Steel",
    sousTitre: "Film de démo DASH",
    url: "https://bitmovin-a.akamaihd.net/content/tears_of_steel/manifest.mpd",
    live: false,
  },
  {
    titre: "Envivio Live",
    sousTitre: "Flux live multi-bitrate",
    url: "https://wowzaec2demo.streamlock.net/live/bigbuckbunny/manifest_mpm4sav_mvtime.mpd",
    live: true,
  },
  {
    titre: "Sintel",
    sousTitre: "Film d'animation DASH",
    url: "https://bitmovin-a.akamaihd.net/content/sintel/sintel.mpd",
    live: false,
  },
];

const VideoPlayer = () => {
  const videoRef = useRef(null);
  const playerRef = useRef(null);
  const [isLive, setIsLive] = useState(false);
  const [qualities, setQualities] = useState([]);
  const [selectedQuality, setSelectedQuality] = useState("auto");
  const [textTracks, setTextTracks] = useState([]);
  const [selectedText, setSelectedText] = useState("off");
  const [audioTracks, setAudioTracks] = useState([]);
  const [selectedAudio, setSelectedAudio] = useState("auto");
  const [isAtLiveEdge, setIsAtLiveEdge] = useState(true);
  const [showPlaylist, setShowPlaylist] = useState(false);

  // L'index de la première VOD pour l'initialisation
  const firstVodIndex = PLAYLIST.findIndex((v) => !v.live);
  const [currentIndex, setCurrentIndex] = useState(
    firstVodIndex !== -1 ? firstVodIndex : 0
  );

  // Déplacer ces lignes ici, après currentIndex
  const currentVideo = PLAYLIST[currentIndex];
  const isCurrentlyLive = currentVideo.live;

  // Séparer la playlist VOD de la playlist totale
  const VOD_PLAYLIST = PLAYLIST.filter((v) => !v.live);

  useEffect(() => {
    // Détruire l'ancienne instance avant d'en créer une nouvelle
    if (playerRef.current) {
      playerRef.current.destroy();
      playerRef.current = null;
    }
    if (videoRef.current) {
      videoRef.current.removeEventListener("timeupdate", handleLiveEdgeCheck);
    }
    const initPlayer = async (
      url = currentVideo.url,
      live = currentVideo.live
    ) => {
      if (!videoRef.current) return;
      const player = new shaka.Player(videoRef.current);
      playerRef.current = player;
      const drmConfig = {
        servers: {
          "com.widevine.alpha": "https://license.widevine.com/getlicense",
        },
      };
      player.configure("drm", drmConfig);
      const adConfig = {
        adTagUrl:
          "https://pubads.g.doubleclick.net/gampad/ads?iu=/21775744923/external/single_preroll_skippable&sz=640x480&ciu_szs=300x250%2C728x90&gdfp_req=1&output=vast&unviewed_position_start=1&env=vp&impl=s&correlator=",
      };
      player.configure("ad", adConfig);
      player.addEventListener("adaptation", () => {
        const tracks = player.getVariantTracks();
        setQualities(tracks);
        setAudioTracks(
          player
            .getVariantTracks()
            .filter(
              (t, i, arr) =>
                arr.findIndex((tt) => tt.language === t.language) === i
            )
        );
        setTextTracks(player.getTextTracks());
      });
      try {
        await player.load(url);
        setIsLive(live);
        const tracks = player.getVariantTracks();
        setQualities(tracks);
        setAudioTracks(
          player
            .getVariantTracks()
            .filter(
              (t, i, arr) =>
                arr.findIndex((tt) => tt.language === t.language) === i
            )
        );
        setTextTracks(player.getTextTracks());
        if (videoRef.current) {
          videoRef.current.play().catch(() => {});
        }
        // Gestion du live edge
        if (live) {
          setIsAtLiveEdge(true);
          videoRef.current.addEventListener("timeupdate", handleLiveEdgeCheck);
        }
      } catch (error) {
        console.error("Erreur lors du chargement de la vidéo:", error);
      }
      // Passage à la vidéo suivante à la fin
      videoRef.current.onended = () => {
        if (currentIndex < PLAYLIST.length - 1) {
          setCurrentIndex(currentIndex + 1);
        }
      };
    };
    initPlayer();
    return () => {
      if (playerRef.current) {
        playerRef.current.destroy();
        playerRef.current = null;
      }
      if (videoRef.current) {
        videoRef.current.removeEventListener("timeupdate", handleLiveEdgeCheck);
      }
    };
    // eslint-disable-next-line
  }, [currentIndex]);

  // Fonction pour vérifier si on est à la pointe du live
  const handleLiveEdgeCheck = () => {
    if (!videoRef.current || !playerRef.current || !playerRef.current.isLive())
      return;
    const liveEdge = playerRef.current.seekRange().end;
    const current = videoRef.current.currentTime;
    // Si on est à moins de 2 secondes de la fin du buffer, on est "en direct"
    setIsAtLiveEdge(liveEdge - current < 2);
  };

  // En mode live, désactiver le seek
  useEffect(() => {
    if (isCurrentlyLive && videoRef.current) {
      videoRef.current.addEventListener("seeking", preventSeekLive);
      return () => {
        videoRef.current.removeEventListener("seeking", preventSeekLive);
      };
    }
  }, [isCurrentlyLive]);

  const preventSeekLive = (e) => {
    if (!playerRef.current || !playerRef.current.isLive()) return;
    const liveEdge = playerRef.current.seekRange().end;
    // Si l'utilisateur tente de reculer, on le remet à la pointe
    if (videoRef.current.currentTime < liveEdge - 2) {
      videoRef.current.currentTime = liveEdge;
    }
  };

  // Bouton revenir au direct
  const goToLiveEdge = () => {
    if (playerRef.current && videoRef.current && playerRef.current.isLive()) {
      const liveEdge = playerRef.current.seekRange().end;
      videoRef.current.currentTime = liveEdge;
      setIsAtLiveEdge(true);
    }
  };

  const avancer = () => {
    if (videoRef.current) {
      videoRef.current.currentTime += 10;
    }
  };
  const reculer = () => {
    if (videoRef.current) {
      videoRef.current.currentTime -= 10;
    }
  };
  const handleQualityChange = (e) => {
    const value = e.target.value;
    setSelectedQuality(value);
    if (playerRef.current) {
      if (value === "auto") {
        playerRef.current.configure({ abr: { enabled: true } });
      } else {
        playerRef.current.configure({ abr: { enabled: false } });
        const track = qualities.find((q) => q.id.toString() === value);
        if (track) {
          playerRef.current.selectVariantTrack(track, /* clearBuffer= */ true);
        }
      }
    }
  };
  const handleTextChange = (e) => {
    const value = e.target.value;
    setSelectedText(value);
    if (playerRef.current) {
      if (value === "off") {
        playerRef.current.setTextTrackVisibility(false);
      } else {
        playerRef.current.setTextTrackVisibility(true);
        const track = textTracks.find((t) => t.language === value);
        if (track) {
          playerRef.current.selectTextLanguage(track.language);
        }
      }
    }
  };
  const handleAudioChange = (e) => {
    const value = e.target.value;
    setSelectedAudio(value);
    if (playerRef.current) {
      if (value === "auto") {
        playerRef.current.configure({ abr: { enabled: true } });
      } else {
        playerRef.current.configure({ abr: { enabled: false } });
        const track = audioTracks.find((t) => t.language === value);
        if (track) {
          playerRef.current.selectAudioLanguage(track.language);
        }
      }
    }
  };
  const handleSelectVideo = (idx) => {
    setCurrentIndex(idx);
  };
  const handleGoLiveOrVod = () => {
    if (isCurrentlyLive) {
      // Retour au premier flux VOD
      const firstVodIndex = PLAYLIST.findIndex((v) => !v.live);
      if (firstVodIndex !== -1) {
        setCurrentIndex(firstVodIndex);
      }
    } else {
      // Aller au premier flux live
      const liveIndex = PLAYLIST.findIndex((v) => v.live);
      if (liveIndex !== -1) {
        setCurrentIndex(liveIndex);
        if (
          liveIndex === currentIndex &&
          videoRef.current &&
          playerRef.current
        ) {
          playerRef.current.load(PLAYLIST[liveIndex].url).then(() => {
            setIsLive(true);
            videoRef.current.play().catch(() => {});
          });
        }
      }
    }
  };
  const handleOpenPlaylist = () => setShowPlaylist(true);
  const handleClosePlaylist = () => setShowPlaylist(false);

  return (
    <div className="lecteur-container">
      <div className="lecteur-header">
        <span className="lecteur-retour">&#8592;</span>
        <div>
          <div className="lecteur-titre">{currentVideo.titre}</div>
          <div className="lecteur-sous-titre">{currentVideo.sousTitre}</div>
        </div>
      </div>
      <div className="lecteur-video-wrapper">
        <video
          ref={videoRef}
          key={currentIndex}
          controls
          className="lecteur-video"
        />
        {isLive && (
          <span
            className={
              "lecteur-live" + (isAtLiveEdge ? "" : " lecteur-live-retard")
            }
          >
            {isAtLiveEdge ? "EN DIRECT" : "EN RETARD"}
          </span>
        )}
        {isLive && !isAtLiveEdge && (
          <button className="lecteur-bouton-live-retard" onClick={goToLiveEdge}>
            Revenir au direct
          </button>
        )}
      </div>
      <div className="lecteur-boutons">
        <button onClick={reculer} className="lecteur-bouton">
          ⏪ 10s
        </button>
        <button onClick={avancer} className="lecteur-bouton">
          10s ⏩
        </button>
        <button onClick={handleGoLiveOrVod} className="lecteur-bouton-live">
          {isCurrentlyLive ? "Retour VOD" : "Passer en direct"}
        </button>
        <select
          value={selectedQuality}
          onChange={handleQualityChange}
          className="lecteur-select-qualite"
        >
          <option value="auto">Qualité : Auto</option>
          {qualities
            .filter(
              (q, idx, arr) =>
                arr.findIndex((t) => t.height === q.height) === idx
            )
            .sort((a, b) => b.height - a.height)
            .map((q) => (
              <option key={q.id} value={q.id}>
                {q.height ? q.height + "p" : q.bandwidth + "bps"}
              </option>
            ))}
        </select>
        <select
          value={selectedAudio}
          onChange={handleAudioChange}
          className="lecteur-select-qualite"
        >
          <option value="auto">Audio : Auto</option>
          {audioTracks
            .filter(
              (a, idx, arr) =>
                arr.findIndex((t) => t.language === a.language) === idx
            )
            .map((a) => (
              <option key={a.language} value={a.language}>
                {a.language}
              </option>
            ))}
        </select>
        <select
          value={selectedText}
          onChange={handleTextChange}
          className="lecteur-select-qualite"
        >
          <option value="off">Sous-titres : Aucun</option>
          {textTracks
            .filter(
              (t, idx, arr) =>
                arr.findIndex((tt) => tt.language === t.language) === idx
            )
            .map((t) => (
              <option key={t.language} value={t.language}>
                {t.language}
              </option>
            ))}
        </select>
        <button
          onClick={handleOpenPlaylist}
          className="lecteur-bouton-playlist"
        >
          Playlist
        </button>
      </div>
      {showPlaylist && (
        <div className="lecteur-playlist-popup">
          <button
            className="lecteur-playlist-close"
            onClick={handleClosePlaylist}
          >
            X
          </button>
          <div className="lecteur-playlist-list">
            {VOD_PLAYLIST.map((video, idx) => {
              // Trouver l'index réel dans PLAYLIST pour la sélection
              const realIdx = PLAYLIST.findIndex((v) => v.url === video.url);
              return (
                <button
                  key={video.url}
                  className={`lecteur-playlist-item${
                    realIdx === currentIndex ? " active" : ""
                  }`}
                  onClick={() => {
                    handleSelectVideo(realIdx);
                    handleClosePlaylist();
                  }}
                >
                  {video.titre}{" "}
                  <span style={{ fontSize: "0.9em", color: "#bdbdbd" }}>
                    ({video.sousTitre})
                  </span>
                </button>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default VideoPlayer;
