import React from "react";
import Button from "./Button";

export default function PlayerControls({
  avancer,
  reculer,
  handleGoLiveOrVod,
  isCurrentlyLive,
  selectedQuality,
  handleQualityChange,
  qualities,
  selectedAudio,
  handleAudioChange,
  audioTracks,
  selectedText,
  handleTextChange,
  textTracks,
  handleOpenPlaylist,
}) {
  return (
    <div className="lecteur-boutons">
      <Button onClick={reculer}>⏪ 10s</Button>
      <Button onClick={avancer}>10s ⏩</Button>
      <Button onClick={handleGoLiveOrVod} variant="live">
        {isCurrentlyLive ? "Retour VOD" : "Passer en direct"}
      </Button>
      <select
        value={selectedQuality}
        onChange={handleQualityChange}
        className="lecteur-select-qualite"
      >
        <option value="auto">Qualité : Auto</option>
        {qualities
          .filter(
            (q, idx, arr) => arr.findIndex((t) => t.height === q.height) === idx
          )
          .sort((a, b) => b.height - a.height)
          .map((q) => (
            <option key={q.id} value={q.id}>Qualité : 
              { q.height ? + " " + q.height + "p" : q.bandwidth + "bps"}
            </option>
          ))}
      </select>
      <select
        value={selectedAudio}
        onChange={handleAudioChange}
        className="lecteur-select-qualite"
      >
        <option value="auto">Audio : Auto</option>
        {audioTracks
          .filter(
            (a, idx, arr) =>
              arr.findIndex((t) => t.language === a.language) === idx
          )
          .map((a) => (
            <option key={a.language} value={a.language}>
              Audio :{a.language}
            </option>
          ))}
      </select>
      <select
        value={selectedText}
        onChange={handleTextChange}
        className="lecteur-select-qualite"
      >
        <option value="off">Sous-titres : Aucun</option>
        {textTracks
          .filter(
            (t, idx, arr) =>
              arr.findIndex((tt) => tt.language === t.language) === idx
          )
          .map((t) => (
            <option key={t.language} value={t.language}>
             Sous-titres : {t.language}
            </option>
          ))}
      </select>
      <Button onClick={handleOpenPlaylist} variant="playlist">
        Playlist
      </Button>
    </div>
  );
}
