import React from "react";

export default function PlayerHeader({ titre, sousTitre, onBack }) {
  return (
    <div className="lecteur-header">
      <button
        className="lecteur-retour"
        onClick={onBack}
        aria-label="Retour à l'accueil"
      >
        <span className="lecteur-retour">&#8592;</span>Retour à l'accueil
      </button>
      <div>
        <div className="lecteur-titre">{titre}</div>
        <div className="lecteur-sous-titre" style={{ color: "red" }}>
          {sousTitre}
        </div>
      </div>
    </div>
  );
}
