import React, { useState, useEffect } from "react";
import "./Navigation.css";

const Navigation = ({ onSearch, onCategoryChange }) => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [isSearchActive, setIsSearchActive] = useState(false);
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);
  const [activeCategory, setActiveCategory] = useState("accueil");

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 100) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleSearchChange = (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    onSearch(query);
  };

  const handleSearchFocus = () => {
    setIsSearchActive(true);
  };

  const handleSearchBlur = () => {
    if (!searchQuery) {
      setIsSearchActive(false);
    }
  };

  const handleCategoryClick = (category) => {
    setActiveCategory(category);
    onCategoryChange(category);
  };

  const handleProfileClick = () => {
    setIsProfileMenuOpen(!isProfileMenuOpen);
  };

  const handleProfileMenuBlur = () => {
    setTimeout(() => {
      setIsProfileMenuOpen(false);
    }, 200);
  };

  const handleProfileAction = (action) => {
    switch (action) {
      case "profile":
        // Gérer l'action du profil
        console.log("Gérer le profil");
        break;
      case "account":
        // Gérer l'action du compte
        console.log("Gérer le compte");
        break;
      case "help":
        // Gérer l'action d'aide
        console.log("Afficher l'aide");
        break;
      case "logout":
        // Gérer la déconnexion
        console.log("Déconnexion");
        break;
      default:
        break;
    }
    setIsProfileMenuOpen(false);
  };

  return (
    <nav className={`navigation ${isScrolled ? "scrolled" : ""}`}>
      <div className="nav-left">
        <img
          src="https://upload.wikimedia.org/wikipedia/commons/0/08/Netflix_2015_logo.svg"
          alt="Logo"
          className="nav-logo"
          onClick={() => handleCategoryClick("accueil")}
        />
        <ul className="nav-links">
          <li>
            <a
              href="#accueil"
              className={activeCategory === "accueil" ? "active" : ""}
              onClick={(e) => {
                e.preventDefault();
                handleCategoryClick("accueil");
              }}
            >
              Accueil
            </a>
          </li>
          <li>
            <a
              href="#series"
              className={activeCategory === "series" ? "active" : ""}
              onClick={(e) => {
                e.preventDefault();
                handleCategoryClick("series");
              }}
            >
              Séries
            </a>
          </li>
          <li>
            <a
              href="#films"
              className={activeCategory === "films" ? "active" : ""}
              onClick={(e) => {
                e.preventDefault();
                handleCategoryClick("films");
              }}
            >
              Films
            </a>
          </li>
          <li>
            <a
              href="#nouveautes"
              className={activeCategory === "nouveautes" ? "active" : ""}
              onClick={(e) => {
                e.preventDefault();
                handleCategoryClick("nouveautes");
              }}
            >
              Nouveautés
            </a>
          </li>
          <li>
            <a
              href="#mylist"
              className={activeCategory === "mylist" ? "active" : ""}
              onClick={(e) => {
                e.preventDefault();
                handleCategoryClick("mylist");
              }}
            >
              Ma Liste
            </a>
          </li>
        </ul>
      </div>
      <div className="nav-right">
        <div className={`search-box ${isSearchActive ? "active" : ""}`}>
          <input
            type="text"
            placeholder="Rechercher..."
            value={searchQuery}
            onChange={handleSearchChange}
            onFocus={handleSearchFocus}
            onBlur={handleSearchBlur}
          />
          <button className="search-button">🔍</button>
        </div>
        <div className="profile-menu" onBlur={handleProfileMenuBlur}>
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/0/0b/Netflix-avatar.png"
            alt="Profile"
            className="profile-icon"
            onClick={handleProfileClick}
          />
          {isProfileMenuOpen && (
            <div className="profile-dropdown">
              <ul>
                <li>
                  <a
                    href="#profile"
                    onClick={(e) => {
                      e.preventDefault();
                      handleProfileAction("profile");
                    }}
                  >
                    Profil
                  </a>
                </li>
                <li>
                  <a
                    href="#account"
                    onClick={(e) => {
                      e.preventDefault();
                      handleProfileAction("account");
                    }}
                  >
                    Compte
                  </a>
                </li>
                <li>
                  <a
                    href="#help"
                    onClick={(e) => {
                      e.preventDefault();
                      handleProfileAction("help");
                    }}
                  >
                    Aide
                  </a>
                </li>
                <li>
                  <a
                    href="#logout"
                    onClick={(e) => {
                      e.preventDefault();
                      handleProfileAction("logout");
                    }}
                  >
                    Déconnexion
                  </a>
                </li>
              </ul>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
