import React, { useState, useEffect } from "react";
import "./HomePage.css";

const HomePage = () => {
  const [featuredContent, setFeaturedContent] = useState(null);
  const [categories, setCategories] = useState([]);

  // Simuler des données de contenu
  useEffect(() => {
    setFeaturedContent({
      title: "Big Buck Bunny",
      description:
        "Un film d'animation open source qui suit les aventures de Big Buck Bunny.",
      imageUrl:
        "https://storage.googleapis.com/gtv-videos-bucket/sample/images/BigBuckBunny.jpg",
      videoUrl: "https://dash.akamaized.net/akamai/bbb_30fps/bbb_30fps.mpd",
    });

    setCategories([
      {
        title: "Films Populaires",
        items: [
          {
            title: "Sintel",
            imageUrl:
              "https://storage.googleapis.com/gtv-videos-bucket/sample/images/Sintel.jpg",
            videoUrl:
              "https://storage.googleapis.com/shaka-demo-assets/sintel/dash.mpd",
          },
          {
            title: "Tears of Steel",
            imageUrl:
              "https://storage.googleapis.com/gtv-videos-bucket/sample/images/TearsOfSteel.jpg",
            videoUrl:
              "https://storage.googleapis.com/shaka-demo-assets/tears-of-steel/dash.mpd",
          },
        ],
      },
      {
        title: "Contenu en Direct",
        items: [
          {
            title: "Live Test",
            imageUrl:
              "https://storage.googleapis.com/gtv-videos-bucket/sample/images/ElephantsDream.jpg",
            videoUrl:
              "https://livesim.dashif.org/livesim/testpic_2s/Manifest.mpd",
            isLive: true,
          },
        ],
      },
    ]);
  }, []);

  return (
    <div className="home-page">
      {/* Bannière principale */}
      <div
        className="featured-content"
        style={{
          backgroundImage: `linear-gradient(to bottom, rgba(0,0,0,0.4), rgba(0,0,0,0.8)), url(${featuredContent?.imageUrl})`,
        }}
      >
        <div className="featured-content-info">
          <h1>{featuredContent?.title}</h1>
          <p>{featuredContent?.description}</p>
          <div className="featured-buttons">
            <button className="play-button">▶ Lecture</button>
            <button className="more-info-button">ℹ Plus d'infos</button>
          </div>
        </div>
      </div>

      {/* Catégories de contenu */}
      <div className="content-categories">
        {categories.map((category, index) => (
          <div key={index} className="category">
            <h2 className="category-title">{category.title}</h2>
            <div className="category-items">
              {category.items.map((item, itemIndex) => (
                <div key={itemIndex} className="content-item">
                  <div className="content-item-image">
                    <img src={item.imageUrl} alt={item.title} />
                    {item.isLive && (
                      <span className="live-badge">EN DIRECT</span>
                    )}
                  </div>
                  <h3>{item.title}</h3>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
