import React from "react";

export default function PlayerControls({
  avancer,
  reculer,
  handleGoLiveOrVod,
  isCurrentlyLive,
  selectedQuality,
  handleQualityChange,
  qualities,
  selectedAudio,
  handleAudioChange,
  audioTracks,
  selectedText,
  handleTextChange,
  textTracks,
  handleOpenPlaylist,
}) {
  return (
    <div className="lecteur-boutons">
      <button onClick={reculer} className="lecteur-bouton">
        ⏪ 10s
      </button>
      <button onClick={avancer} className="lecteur-bouton">
        10s ⏩
      </button>
      <button onClick={handleGoLiveOrVod} className="lecteur-bouton-live">
        {isCurrentlyLive ? "Retour VOD" : "Passer en direct"}
      </button>
      <select
        value={selectedQuality}
        onChange={handleQualityChange}
        className="lecteur-select-qualite"
      >
        <option value="auto">Qualité : Auto</option>
        {qualities
          .filter(
            (q, idx, arr) => arr.findIndex((t) => t.height === q.height) === idx
          )
          .sort((a, b) => b.height - a.height)
          .map((q) => (
            <option key={q.id} value={q.id}>
              {q.height ? q.height + "p" : q.bandwidth + "bps"}
            </option>
          ))}
      </select>
      <select
        value={selectedAudio}
        onChange={handleAudioChange}
        className="lecteur-select-qualite"
      >
        <option value="auto">Audio : Auto</option>
        {audioTracks
          .filter(
            (a, idx, arr) =>
              arr.findIndex((t) => t.language === a.language) === idx
          )
          .map((a) => (
            <option key={a.language} value={a.language}>
              {a.language}
            </option>
          ))}
      </select>
      <select
        value={selectedText}
        onChange={handleTextChange}
        className="lecteur-select-qualite"
      >
        <option value="off">Sous-titres : Aucun</option>
        {textTracks
          .filter(
            (t, idx, arr) =>
              arr.findIndex((tt) => tt.language === t.language) === idx
          )
          .map((t) => (
            <option key={t.language} value={t.language}>
              {t.language}
            </option>
          ))}
      </select>
      <button onClick={handleOpenPlaylist} className="lecteur-bouton-playlist">
        Playlist
      </button>
    </div>
  );
}
