const HomePage = () => {
  const [featuredContent, setFeaturedContent] = useState(null);
  const [categories, setCategories] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentVideo, setCurrentVideo] = useState(null);
  const [searchResults, setSearchResults] = useState(null);

  useEffect(() => {
    // Sélectionner le premier contenu non-live comme contenu en vedette
    const firstNonLive = PLAYLIST.find((item) => !item.live) || PLAYLIST[0];
    setFeaturedContent({
      title: firstNonLive.titre,
      description: firstNonLive.sousTitre,
      imageUrl: firstNonLive.imageUrl,
      videoUrl: firstNonLive.url,
    });

    // Organiser le contenu en catégories
    setCategories([
      {
        title: "Contenu en Direct",
        items: PLAYLIST.filter((item) => item.live),
      },
      {
        title: "Films d'Animation",
        items: PLAYLIST.filter(
          (item) =>
            !item.live &&
            (item.titre.includes("Bunny") ||
              item.titre.includes("Sintel") ||
              item.titre.includes("Elephants"))
        ),
      },
      {
        title: "Documentaires",
        items: PLAYLIST.filter(
          (item) =>
            !item.live &&
            (item.titre.includes("Planète") || item.titre.includes("Univers"))
        ),
      },
      {
        title: "Contenu Éducatif",
        items: PLAYLIST.filter(
          (item) =>
            !item.live &&
            (item.titre.includes("Cours") ||
              item.titre.includes("Histoire") ||
              item.titre.includes("Éducatives"))
        ),
      },
      {
        title: "Sport",
        items: PLAYLIST.filter(
          (item) =>
            !item.live &&
            (item.titre.includes("Football") || item.titre.includes("Tour"))
        ),
      },
      {
        title: "Musique",
        items: PLAYLIST.filter(
          (item) =>
            !item.live &&
            (item.titre.includes("Jazz") ||
              item.titre.includes("Concert") ||
              item.titre.includes("Festival"))
        ),
      },
      {
        title: "Culture",
        items: PLAYLIST.filter(
          (item) =>
            !item.live &&
            (item.titre.includes("Théâtre") || item.titre.includes("Opéra"))
        ),
      },
      {
        title: "Enfants",
        items: PLAYLIST.filter(
          (item) =>
            !item.live &&
            (item.titre.includes("Dessins") || item.titre.includes("Émissions"))
        ),
      },
    ]);
  }, []);

  const handleSearch = (query) => {
    if (!query.trim()) {
      setSearchResults(null);
      return;
    }

    const results = PLAYLIST.filter(
      (item) =>
        item.titre.toLowerCase().includes(query.toLowerCase()) ||
        item.sousTitre.toLowerCase().includes(query.toLowerCase())
    );

    setSearchResults({
      title: "Résultats de recherche",
      items: results,
    });
  };

  const handlePlay = (video) => {
    setCurrentVideo(video);
    setIsPlaying(true);
  };

  const handleClosePlayer = () => {
    setIsPlaying(false);
    setCurrentVideo(null);
  };

  if (isPlaying && currentVideo) {
    return (
      <div className="video-player-container">
        <button className="back-button" onClick={handleClosePlayer}>
          ← Retour à l'accueil
        </button>
        <VideoPlayer
          initialVideo={{
            titre: currentVideo.titre,
            sousTitre: currentVideo.sousTitre,
            url: currentVideo.url,
            live: currentVideo.live,
          }}
        />
      </div>
    );
  }

  return (
    <div className="home-page">
      <Navigation onSearch={handleSearch} />

      {searchResults ? (
        <div className="content-categories">
          <div className="category">
            <h2 className="category-title">{searchResults.title}</h2>
            <div className="category-items">
              {searchResults.items.map((item, index) => (
                <div
                  key={index}
                  className="content-item"
                  onClick={() => handlePlay(item)}
                >
                  <div className="content-item-image">
                    <img src={item.imageUrl} alt={item.titre} />
                    {item.live && <span className="live-badge">EN DIRECT</span>}
                    <div className="content-item-overlay">
                      <button className="play-button-small">▶</button>
                    </div>
                  </div>
                  <h3>{item.titre}</h3>
                  <p className="content-item-subtitle">{item.sousTitre}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      ) : (
        <>
          {/* Bannière principale */}
          <div
            className="featured-content"
            style={{
              backgroundImage: `linear-gradient(to bottom, rgba(0,0,0,0.4), rgba(0,0,0,0.8)), url(${featuredContent?.imageUrl})`,
            }}
          >
            <div className="featured-content-info">
              <h1>{featuredContent?.title}</h1>
              <p>{featuredContent?.description}</p>
              <div className="featured-buttons">
                <button
                  className="play-button"
                  onClick={() =>
                    handlePlay({
                      titre: featuredContent.title,
                      sousTitre: featuredContent.description,
                      url: featuredContent.videoUrl,
                      live: false,
                    })
                  }
                >
                  ▶ Lecture
                </button>
                <button className="more-info-button">ℹ Plus d'infos</button>
              </div>
            </div>
          </div>

          {/* Catégories de contenu */}
          <div className="content-categories">
            {categories.map((category, index) => (
              <div key={index} className="category">
                <h2 className="category-title">{category.title}</h2>
                <div className="category-items">
                  {category.items.map((item, itemIndex) => (
                    <div
                      key={itemIndex}
                      className="content-item"
                      onClick={() => handlePlay(item)}
                    >
                      <div className="content-item-image">
                        <img src={item.imageUrl} alt={item.titre} />
                        {item.live && (
                          <span className="live-badge">EN DIRECT</span>
                        )}
                        <div className="content-item-overlay">
                          <button className="play-button-small">▶</button>
                        </div>
                      </div>
                      <h3>{item.titre}</h3>
                      <p className="content-item-subtitle">{item.sousTitre}</p>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default HomePage;
